arguments.hosts:
{%- set hosts=[] %}
{%- if "Hosts:" in vars.item.data.data.alertInfo.subject %}
  {%- for host in vars.item.data.data.alertInfo.subject[:-1].split("Hosts:")[1].split(";")[0].split(",") %}
      {%- if hosts.append(host.strip()) %}{% endif %}
  {%- endfor %}
{%- endif %}
{{hosts | unique}}

arguments.users:
{%- set users=[] %}
{%- if "Users:" in vars.item.data.data.alertInfo.subject and vars.item.data.data.alertType == "PotentiallyCompromisedAwsCredentials" %}
  {%- for user in vars.item.data.data.alertInfo.subject[:-1].split("Users:")[1].split(";")[0].split(",") %}
      {%- if users.append(user.strip()) %}{% endif %}
  {%- endfor %}
{%- endif %}
{{users | unique}}

arguments.ip_iocs:
{%- set matches=[] %}
{%- if "Users:" in vars.item.data.data.alertInfo.description %}
	{%- for ip in vars.item.data.data.entityMap.SourceIpAddress %}
  		{%- if matches.append(ip.KEY.ip_addr) %}{% endif %}
  	{%- endfor %}
{%- endif %}
{%- for fact in vars.item.data.data.alertInfo.supportingFacts %}
  {%- for indicator in fact.subElements %}
      {%- for sub in indicator.subElements %}
        {%- if 'The involved remote host(s):' in sub.supportingFactText or 'Observed destination hosts include:' in sub.supportingFactText %}
          {%- for sub_fact in sub.subElements %}
            {%- if (sub_fact.supportingFactText | regex_search("([0-9]{1,3}\\.){3}[0-9]{1,3}")) %}      
              {%- if matches.append(sub_fact.supportingFactText) %}{% endif %}
            {%- endif %}
          {%- endfor %} 
        {%- endif %}
      {%- endfor %} 
  {%- endfor %}                           
{%- endfor %}
{{ matches | unique }}

arguments.machines:
{%- set hosts=[] %}
{%- set instanceIds = [] %}
{%- set machines = [] %}
{%- if "Hosts:" in vars.item.data.data.alertInfo.subject %}
  {%- for host in vars.item.data.data.alertInfo.subject[:-1].split("Hosts:")[1].split(";")[0].split(",") %}
      {%- if hosts.append(host.strip()) %}{% endif %}
  {%- endfor %}
  {%- for machine in vars.item.data.data.entityMap.Machine %}
      {%- if (machine.PROPS.tags.Hostname in hosts or machine.PROPS.hostname in hosts) and "tags" in machine.PROPS and machine.PROPS.tags.VmProvider == "AWS" and "InstanceId" in machine.PROPS.tags and "Account" in machine.PROPS.tags and "Zone" in machine.PROPS.tags and machine.PROPS.tags.InstanceId not in instanceIds %}
          {%- if machines.append(machine) %}{% endif %}
          {%- if instanceIds.append(machine.PROPS.tags.InstanceId) %}{% endif %}
      {%- endif %}
  {%- endfor %}
{%- endif %}
{{ machines | unique}}

arguments.url_iocs:
{%- set matches=[] %}
{%- for fact in vars.item.data.data.entityMap.Process %}
  {%- if fact.PROPS is defined and fact.PROPS.cmdline is defined and (fact.PROPS.cmdline|regex_search('https?://[^\\s]+')) %}
    {%- if matches.append(fact.PROPS.cmdline|regex_search('https?://[^\\s]+', '\\0')) %}{% endif %}
  {%- endif %}
{%- endfor %}
{{ matches | unique }}

arguments.domain_iocs:
{%- set matches=[] %}
{%- for fact in vars.item.data.data.alertInfo.supportingFacts %}
  {%- for indicator in fact.subElements %}
      {%- for sub in indicator.subElements %}
        {%- if 'The involved remote host(s):' in sub.supportingFactText or 'Observed destination hosts include:' in sub.supportingFactText %}
          {%- for sub_fact in sub.subElements %}
            {%- if not (sub_fact.supportingFactText |regex_search("([0-9]{1,3}\\.){3}[0-9]{1,3}")) %}      
              {%- if matches.append(sub_fact.supportingFactText) %}{% endif %}
            {%- endif %}
          {%- endfor %} 
        {%- endif %}
      {%- endfor %} 
  {%- endfor %}                           
{%- endfor %}
{{ matches | unique }}

arguments.instanceIds:
{%- set hosts=[] %}
{%- set instanceIds = [] %}
{%- set machines = [] %}
{%- if "Hosts:" in vars.item.data.data.alertInfo.subject %}
  {%- for host in vars.item.data.data.alertInfo.subject[:-1].split("Hosts:")[1].split(";")[0].split(",") %}
      {%- if hosts.append(host.strip()) %}{% endif %}
  {%- endfor %}

  {%- for machine in vars.item.data.data.entityMap.Machine %}
      {%- if (machine.PROPS.tags.Hostname in hosts or machine.PROPS.hostname in hosts) and "tags" in machine.PROPS and machine.PROPS.tags.VmProvider == "AWS" and "InstanceId" in machine.PROPS.tags and "Account" in machine.PROPS.tags and "Zone" in machine.PROPS.tags and machine.PROPS.tags.InstanceId not in instanceIds %}
        {%- if machines.append(machine) %}{% endif %}
        {%- if instanceIds.append(machine.PROPS.tags.InstanceId) %}{% endif %}
      {%- endif %}
  {%- endfor %}
{%- endif %}
{{ instanceIds | unique }}

arguments.cmdline_iocs:
{%- set matches=[] %}
{%- for fact in vars.item.data.data.alertInfo.supportingFacts %}
  {%- for indicator in fact.subElements %}
      {%- for sub in indicator.subElements %}
        {%- if 'Example command line statements include:' in sub.supportingFactText %}
          {%- for sub_fact in sub.subElements %}
            {%- if matches.append(sub_fact.supportingFactText) %}{% endif %}
          {%- endfor %} 
        {%- endif %}
      {%- endfor %} 
  {%- endfor %}                           
{%- endfor %}
{%- for fact in vars.item.data.data.entityMap.Process %}
  {%- if fact.PROPS is defined and fact.PROPS.cmdline is defined and ("/dev/tcp" in fact.PROPS.cmdline or "http" in fact.PROPS.cmdline) %}
    {%- if matches.append(fact.PROPS.cmdline) %}{% endif %}
  {%- endif %}
{%- endfor %}
{{ matches | unique }}

